import React, { Component } from 'react';

export default class createExercise extends Component{
    constructor(props) {
        super(props);

        //we want to this refer to the class "createExercise"
        //in order to make sure 'this' refer to the right thing,
        //we bind this with with these methods.
        this.onChangeUsername = this.onChangeUsername.bind(this);
        this.onChangeDescription = this.onChangeDescription.bind(this);
        this.onChangeDuration = this.onChangeDuration.bind(this);
        this.onChangeDate = this.onChangeDate.bind(this);

        this.state = {
            username: '',
            description: '',
            duration: 0,
            date: new Date(),
            users: [],
        }
    }

    //a life circle function in react: after everything display on page
    //the function will be automatically called
    componentDidMount() {
        this.setState({
            users: ['test user'],
            username: 'test user',
        })
    }

    onChangeUsername(e) {
        this.setState({
            username: e.target.value,
        })
    }

    onChangeDescription(e){
        this.setState({
            description:e.target.value,
        })
    }

    onChangeDuration(e){
        this.setState({
            duration: e.target.value,
        })
    }

    onChangeDate(e) {
        this.setState({
            date: e.target.value,
        })
    }

    onSubmit(e){
        e.preventDefault();

        const exercise = {
            username: this.state.username,
            description: this.state.description,
            duration: this.state.duration,
            date: this.state.date,
        }

        console.log(exercise)

        window.location = '/'
    }

    render() {
        return(
            <div>
                <p>Yyyyyyyyyyyy</p>
            </div>
        )
    }
}
